import React, { useEffect, useState } from 'react';
import { MaterialReactTable, useMaterialReactTable } from 'material-react-table';
import axios from 'axios';

const monthNames = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];

const columns = [
  {
    accessorKey: 'id',
    header: 'Id',
    size: 100,
    enableColumnFilter: false,
    enableEditing: false,
    enableSorting: false,
  },
  {
    accessorKey: 'emp_name',
    header: 'Employee Name',
    size: 150,
    enableEditing: false,
    enableColumnFilter: true,
  },
];

monthNames.forEach((month) => {

  columns.push({
    header: month,
    footer: 'Total',
    accessorKey: month.toLowerCase(),
    // accessorKey: `month_name_${index}`,
    // id: "month_name",
    size: 100,
    enableEditing: true,
    enableColumnFilter: true,
  });
});

const FetchEmployee = ({ employeeRefetch, setEmployeeRefetech }) => {
  const [data, setData] = useState([]);
  const [billingData, setBillingData] = useState([]);
  const [combinedData, setCombinedData] = useState([]);

  useEffect(() => {
    axios.get('api/fetchEmployeeBilling').then((response) => {
      const { billing_data, employee_data } = response.data;
      const mergedData = mergeData(billing_data, employee_data);
      setCombinedData(mergedData);
      console.log(mergedData);
      setEmployeeRefetech(false);
    });
  }, [employeeRefetch]);

  const mergeData = (billingData, employeeData) => {
    return billingData.map(billing => {
      const employee = employeeData.find(emp => emp.id === billing.emp_id);
      return {
        id: billing.emp_id,
        // emp_name: employee ? employee.emp_name : 'Unknown',
        month_name: billing.month_name,
        is_billable: billing.is_billable,
      };
    });
  };






  const handleCellEdit = (emp_id, month_name, is_billable) => {
    axios.post("api/createBilling", {
      emp_id, month_name, is_billable
    }).then(() => {
      console.log("Done")

    }).catch(() => console.log("Error"));
  }



  const table = useMaterialReactTable({
    columns,
    data: combinedData,
    enableColumnPinning: true,
    enablePagination: false,
    initialState: {
      columnPinning: { left: ['id', 'emp_name'] },
    },
    enableFullScreenToggle: false,
    enableEditing: true,
    editDisplayMode: 'table', //cell
    columnFilterDisplayMode: 'popover',
    enableEditing: true,
    muiTableBodyCellProps: ({ cell }) => ({
      onBlur: () => {
        // console.log(cell.getValue(), cell.row.original.id, cell.column.id);
        handleCellEdit(cell.row.original.id, cell.column.id, cell.getValue())
      },
    }),
    // onEditingCellChange: handleCellEdit, 

  });

  return (
    <>
      <MaterialReactTable table={table} />
    </>
  );
};

export default FetchEmployee;
