import React, { useEffect, useMemo, useState } from 'react';
import { MaterialReactTable, useMaterialReactTable } from 'material-react-table';
import axios from 'axios';
import { Box, Button, Stack } from '@mui/material';
import { makeStyles } from '@mui/styles';

const useStyles = makeStyles({
  createSaveButtonStyles: {
    // height: '40px',
    // width: '200px',
    fontWeight: '700 !important',
    fontFamily: 'Avenir Next !important',
    color: 'rgb(103, 79, 192)',
  },
});

const monthNames = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];

const columns = [
  {
    accessorKey: 'id',
    header: 'Id',
    size: 100,
    enableColumnFilter: false,
    enableEditing: false,
    enableSorting: false,
  },
  {
    accessorKey: 'emp_name',
    header: 'Employee Name',
    size: 150,
    enableEditing: false,
    enableColumnFilter: true,
  },
];

monthNames.forEach((month) => {
  columns.push({
    header: month,
    // footer: 'Total',
    accessorKey: month.toLowerCase(),
    editVariant: "select",
    editSelectOptions: ["Yes", "No"],
    size: 100,
    enableEditing: true,
    enableColumnFilter: true,
    filterVariant: "select",
    filterSelectOptions: ["Yes", "No"],
    aggregationFn: 'sum',
    Footer: () => (
      <Stack>
        Total:
        {/* <Box color="warning.main">{Math.round(maxYes)}</Box> */}
      </Stack>
    ),
  });

});


const FetchEmployee = ({ employeeRefetch, setEmployeeRefetech }) => {
  const classes = useStyles();
  const [combinedData, setCombinedData] = useState([]);

  useEffect(() => {
    axios.get('api/fetchEmployeeBilling').then((response) => {
      const { billing_data, employee_data } = response.data;
      const mergedData = mergeData(billing_data, employee_data);
      setCombinedData(mergedData);
      setEmployeeRefetech(false);
    });
  }, [employeeRefetch]);

  const mergeData = (billingData, employeeData) => {
    const employeeMap = {};
    console.log(employeeMap)
    employeeData.forEach(employee => {
      employeeMap[employee.id] = {
        id: employee.id,
        emp_name: employee.emp_name,
        ...Object.fromEntries(monthNames.map(month => [month.toLowerCase(), ''])),
      };
    });

    billingData.forEach(billing => {
      const { emp_id, month_name, is_billable } = billing;
      if (employeeMap[emp_id]) {
        employeeMap[emp_id][month_name] = is_billable ? "Yes" : "No";
      }
    });

    return Object.values(employeeMap);
  };


  const handleCellEdit = (emp_id, month_name, is_billable) => {
    axios.post("api/createBilling", {
      resource: {
        emp_id: emp_id,
        month_name: month_name,
        is_billable: is_billable
      }
    }).then(() => {
      console.log("Done")
    }).catch(() => console.log("Error"));
  }
  const handleCellValueChange = (emp_id, month_name, is_billable) => {
    axios.patch("api/editBillingInfo", {
      emp_id, month_name, is_billable
    }).then(() => {
      console.log("Edit Done")
    }).catch(() => {
      console.log("Edit error")
    })
  }

  const table = useMaterialReactTable({
    columns,
    data: combinedData,
    enableColumnPinning: true,
    enablePagination: false,
    initialState: {
      showColumnFilters: true,
      columnPinning: { left: ['id', 'emp_name'] },
    },
    enableFullScreenToggle: false,
    enableEditing: true,
    editDisplayMode: 'cell', //table
    enableEditing: true,
    enableBottomToolbar: false,

    // muiTableBodyCellProps: ({ cell }) => ({
    //   onBlur: () => {
    //     const newValue = cell.getValue();//yes or no
    //     const rowId = cell.row.original.id;//emp_id
    //     const columnId = cell.column.id;//month_name
    //     if (newValue !== cell.row.original[columnId]) {
    //       handleCellValueChange(rowId, columnId, newValue);
    //     }
    //     handleCellEdit(rowId, columnId, newValue);
    //   },
    // }),
    renderTopToolbarCustomActions: ({ table }) => (
      <>
        <Box sx={{ display: "flex", gap: "5px" }}>
          <Button variant="contained" className={classes.createSaveButtonStyles}>Save</Button>
          <Button variant='contained' color='error'>Discard</Button>
        </Box>

      </>
    ),
  });
  return (

    <MaterialReactTable table={table} />

  );
};

export default FetchEmployee;
