import React, { useEffect, useState } from "react";
import {
    MaterialReactTable,
    useMaterialReactTable,
} from "material-react-table";
import axios from "axios";

const monthNames = [
    "Jan",
    "Feb",
    "Mar",
    "Apr",
    "May",
    "Jun",
    "Jul",
    "Aug",
    "Sep",
    "Oct",
    "Nov",
    "Dec",
];

const columns = [
    {
        accessorKey: "id",
        header: "Id",
        size: 100,
        enableEditing: false,
        filterFn: "equals",
        filterVariant: "text",
    },
    {
        accessorKey: "emp_name",
        header: "Employee Name",
        size: 150,
        enableEditing: false,
        enableColumnFilter: true,
        filterFn: "equals",
        filterVariant: "text",
    },
];

monthNames.forEach((month) => {
    columns.push({
        header: month,
        footer: "Total",
        accessorKey: month.toLowerCase(),
        size: 100,
        enableEditing: true,
        enableColumnFilter: true,
        filterFn: "equals",
        filterVariant: "select",
        filterSelectOptions: ["Yes", "No"],
    });
});



const MReactTable = ({ employeeRefetch, setEmployeeRefetech }) => {
    const [data, setData] = useState([]);

    useEffect(() => {
        axios.get("api/fetchEmployee").then((response) => {
            setData(response.data);
            setEmployeeRefetech(false);
        });
    }, [employeeRefetch]);

    const table = useMaterialReactTable({
        columns,
        data,
        enableColumnPinning: true,
        enablePagination: false,
        initialState: {
            columnPinning: { left: ["id", "emp_name"] },

        },
        enableFullScreenToggle: false,
        enableEditing: true,
        editDisplayMode: "table", //cell
        columnFilterDisplayMode: "popover",

    });

    return (
        <>
            <MaterialReactTable table={table} />
        </>
    );
};

export default MReactTable;
