import React, { useEffect, useState } from 'react';
import { MaterialReactTable, useMaterialReactTable } from 'material-react-table';
import axios from 'axios';

const monthNames = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];

const columns = [
  {
    accessorKey: 'id',
    header: 'Id',
    size: 100,
    enableColumnFilter: false,
    enableEditing: false,
    enableSorting: false,
  },
  {
    accessorKey: 'emp_name',
    header: 'Employee Name',
    size: 150,
    enableEditing: false,
    enableColumnFilter: true,
  },
];

monthNames.forEach((month) => {
  columns.push({
    header: month,
    footer: 'Total',
    accessorKey: month.toLowerCase(),
    size: 100,
    enableEditing: true,
    enableColumnFilter: true,
  });
});

const FetchEmployee = ({ employeeRefetch, setEmployeeRefetech }) => {
  const [data, setData] = useState([]);

  useEffect(() => {
    axios.get('api/fetchEmployee').then((response) => {
      setData(response.data);
      setEmployeeRefetech(false);
    });
  }, [employeeRefetch]);

  const handleCellEdit = (updatedRow, oldRow) => {
    const { id, emp_name } = updatedRow;
    for (const [key, value] of Object.entries(updatedRow)) {
      if (monthNames.includes(key)) {
        // Here you can send the updated cell data to the backend
        axios.post('/api/createBilling', {
          emp_id: id,
          emp_name,
          month: key,
          value: value ? 'Yes' : 'No', // Assuming value is a boolean indicating Yes or No
        })
          .then((response) => {
            console.log('Cell data saved successfully:', response.data);
          })
          .catch((error) => {
            console.error('Error saving cell data:', error);
          });
      }
    }
  };




  const table = useMaterialReactTable({
    columns,
    data,
    enableColumnPinning: true,
    enablePagination: false,
    initialState: {
      columnPinning: { left: ['id', 'emp_name'] },
    },
    enableFullScreenToggle: false,
    enableEditing: true,
    editDisplayMode: 'table', //cell
    columnFilterDisplayMode: 'popover',
    enableEditing: true,
    onEditingCellChange: handleCellEdit, // Add this line to handle cell edit events

  });

  return (
    <>
      <MaterialReactTable table={table} />
    </>
  );
};

export default FetchEmployee;
